mapper.select:
  	SELECT id,department_name,parent_id
	FROM department
	WHERE parent_id=#{parentId}

  	SELECT id,department_name,parent_id
	FROM department
	WHERE department_name=#{departmentName}

  	SELECT id,department_name,parent_id
	FROM department
	WHERE id=#{id}

mapper.update:
  	UPDATE department d
	SET d.department_name=#{department.departmentName},d.parent_id=#{department.parentId}
	WHERE d.id = #{department.id}
